import React, { useState } from "react";
import useAxios from "./hooks/useAxios";
import {v4 as uuid} from 'uuid';
import axios from "axios";
import PlayingCard from "./PlayingCard";
import "./PlayingCardList.css";

/* Renders a list of playing cards.
 * Can also add a new card at random. */
function CardTable() {


  const res = useAxios("https://deckofcardsapi.com/api/deck/new/draw/")

  // const c = (res.response)
  const {id, image} = res.cards.image

  // console.log(c.cards.id)

  return (
    <div className="PlayingCardList">
      <h3>Pick a card, any card!</h3>
      <div>
        <button onClick={res.addCard()}>Add a playing card!</button>
      </div>
      <div className="PlayingCardList-card-area">
         {res.cards.map(cardData => (
          <PlayingCard key={cardData.id} front={cardData.image} />
        ))}
      </div>
    </div>
  );
}

CardTable.defaultProps = {};

export default CardTable;
